mapper.insert:
INSERT INTO 
	assignment(
		course_id
		, assignment_name
		, assignment_content
		, startdate
		, enddate
		, createdate
		)						
VALUES(
	#{courseId}
	, #{assignmentName}
	, #{assignmentContent}
	, #{startdate}
	, #{enddate}
	, now()					
	)

mapper.update:
UPDATE assignment
SET updatedate = now()
	, assignment_name=#{assignmentName} 
	, assignment_content=#{assignmentContent}
	, startdate=#{startdate}
	, enddate=#{enddate}
WHERE assignment_id = #{assignmentId}

mapper.select:
SELECT file, assignment_score assignmentScore, createdate, updatedate
FROM submitted_assignment
WHERE user_id = #{userId} AND assignment_id = #{assignmentId}
