mapper.select:
SELECT bq.post_id AS postId, bq.course_id, bq.user_id, u.user_name AS userName, bq.title, bq.hit_count AS hitCount, bq.createdate, (SELECT COUNT(*) FROM board_qna_comment bqc WHERE bqc.post_id = bq.post_id) AS commentCount FROM board_qna bq JOIN users u ON bq.user_id = u.user_id WHERE bq.course_id = #{courseId}
AND ( bq.title LIKE CONCAT('%', #{searchKeyword}, '%') OR bq.content LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY bq.post_id DESC LIMIT #{limit} OFFSET #{offset}
