mapper.select:
SELECT c.course_id AS courseId, c.course_name AS courseName, p.user_name AS profName, c.course_year AS courseYear, c.course_semester AS courseSemester, c.credit AS credit, c.max_capacity AS maxCapacity, c.status AS status, -- ★ 수업 요일/시간 요약 GROUP_CONCAT( CONCAT( CASE DAYOFWEEK(cs.start_datetime) WHEN 2 THEN '월' WHEN 3 THEN '화' WHEN 4 THEN '수' WHEN 5 THEN '목' WHEN 6 THEN '금' WHEN 7 THEN '토' ELSE '일' END, ' ', DATE_FORMAT(cs.start_datetime, '%H:%i'), '~', DATE_FORMAT(cs.end_datetime, '%H:%i') ) ORDER BY cs.start_datetime SEPARATOR ', ' ) AS scheduleSummary FROM course c JOIN users p ON c.user_id = p.user_id -- 담당 교수 JOIN users s ON s.user_id = #{studentId} -- 신청 학생 LEFT JOIN course_schedule cs ON cs.course_id = c.course_id AND cs.schedule_type = 'CLASS' -- 수업 시간만
LEFT JOIN ( SELECT cr.course_id, COUNT(*) AS enrolled_count FROM course_registration cr WHERE cr.status = 'ENROLLED' GROUP BY cr.course_id ) ec ON c.course_id = ec.course_id
WHERE 1=1 AND c.status = 'OPEN' AND p.department_id = s.department_id AND NOT EXISTS ( SELECT 1 FROM course_registration cr2 WHERE cr2.course_id = c.course_id AND cr2.user_id = #{studentId} AND cr2.status = 'ENROLLED' ) AND IFNULL(ec.enrolled_count, 0) < c.max_capacity
AND c.course_year = #{year}
AND c.course_semester = #{semester}
GROUP BY c.course_id, c.course_name, p.user_name, c.course_year, c.course_semester, c.credit, c.max_capacity, c.status ORDER BY c.course_name ASC

SELECT c.course_id AS courseId, c.course_name AS courseName, p.user_name AS profName, c.course_year AS courseYear, c.course_semester AS courseSemester, c.credit AS credit, c.status AS status FROM course_registration cr JOIN course c ON cr.course_id = c.course_id JOIN users p ON c.user_id = p.user_id WHERE cr.user_id = #{studentId} AND cr.status = 'ENROLLED'
AND c.course_year = #{year}
AND c.course_semester = #{semester}
ORDER BY c.course_name ASC

mapper.update:
UPDATE course_registration
SET status    = 'CANCELED',
    updatedate = NOW()
WHERE user_id  = #{studentId}
  AND course_id = #{courseId}
  AND status   = 'ENROLLED'

mapper.select:
SELECT
    COUNT(*) 
FROM course_registration cr
JOIN course c
  ON cr.course_id = c.course_id
WHERE cr.user_id = #{studentId}
  AND cr.status  = 'ENROLLED'

    AND c.course_year = #{year}


    AND c.course_semester = #{semester}


SELECT COUNT(*)
FROM course_registration
WHERE course_id = #{courseId}
  AND user_id = #{studentId}
  AND status = 'ENROLLED'

SELECT c.course_id AS course_id, c.course_name AS course_name, p.user_name AS professor_name, DAYOFWEEK(cs.start_datetime) AS day_of_week, (HOUR(cs.start_datetime) - 9) + 1 AS period FROM course_registration cr JOIN course c ON cr.course_id = c.course_id JOIN users p ON c.user_id = p.user_id JOIN course_schedule cs ON cs.course_id = c.course_id WHERE cr.user_id = #{studentId} AND cr.status = 'ENROLLED' AND cs.schedule_type = 'CLASS'
AND c.course_year = #{year}
AND c.course_semester = #{semester}
